pragma solidity ^0.5.0;

import "./LockableWithDataRevert.sol";

contract ExampleWithRevert is LockableWithDataRevert{

    mapping(bytes32 => uint256) public data;
    uint256 public single;

    event Dismounted(uint256 data);


    function getData(string memory location) view public returns (uint256) {
        bytes32 hashKey = getHashKey(location);
        return data[hashKey];
    }

    function setData(string memory key, uint256 value) 
    public payable 
    {
       //require(!isLocked("data", getHashKey(key), msg.sender), "data is locked and msg.sender is not locker");
       LockStatus lockStatus;
       uint256 revertData;
       (lockStatus, revertData) = lockWithRevertUint("data", getHashKey(key), msg.sender, value);
       if(lockStatus == LockStatus.UserWithRevert) {
           data[keccak256(abi.encodePacked(key))] = revertData;
           emit Dismounted(data[keccak256(abi.encodePacked(key))]);
       }

       data[keccak256(abi.encodePacked(key))] = value;



    }





    function release(address payable locker)
    public
    {
        unlock(locker);
    }


}
