pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

import "./TypesToBytes.sol";
import "./BytesToTypes.sol";

contract LockableWithDataRevert is TypesToBytes, BytesToTypes{

    struct Record {
        bytes32 data;
        bytes32 key;
    }

    enum Type {Bool, Address, String, Bytes32, Int, Uint} //TODO enhance support for all Int and Uints
    enum LockStatus{User, Others, UserWithRevert}

    struct DataRecord {
        address payable lockStatus;
        bytes dataBeforeLock;
        Type dataType;
    }

    struct LockMap {
        mapping(bytes32 => DataRecord) records;
    }

    struct UserRecord {
        Record[]  records;
        uint256   expiry;
        uint256   deposit;
        uint256   lastLockTime;
    }

    uint public TIMEOUT = 10; 
    uint public DEPOSIT_FEE = 0.01 ether ;
    uint public COOLING_OFF = 20;
    bool public scaleRefund = true;

    //Takes in name of data structure and returns the LockMap for data structure.
    mapping(bytes32 => LockMap) dataRecords;
    mapping(address => UserRecord) public userRecords;




    function saveData(string memory nameOfData, bytes32 key, bytes memory data, Type dataType) internal{
        bytes32 hashKey = getHashKey(nameOfData);
        DataRecord storage record = dataRecords[hashKey].records[key];
        record.dataBeforeLock = data;
        record.dataType = dataType;
    }

    // function lockWithRevertBool(string memory nameOfData, bytes32 key, address payable locker, bool data)
    // public returns(bool)
    // {
    //     if(!lock(nameOfData, key, locker)) return false;

    // }

    // function lockWithRevertAddress(string memory nameOfData, bytes32 key, address payable locker, address data)
    // public returns(bool)
    // {
    //     if(!lock(nameOfData, key, locker)) return false;

    // }

    // function lockWithRevertString(string memory nameOfData, bytes32 key, address payable locker, string data)
    // public returns(bool)
    // {
    //     if(!lock(nameOfData, key, locker)) return false;

    // }

    // function lockWithRevertBytes32(string memory nameOfData, bytes32 key, address payable locker, bytes32 data)
    // public returns(bool)
    // {
    //     if(!lock(nameOfData, key, locker)) return false;

    // }

    // function lockWithRevertInt(string memory nameOfData, bytes32 key, address payable locker, int data)
    // public returns(bool)
    // {
    //     if(!lock(nameOfData, key, locker)) return false;

    // }

    function lockWithRevertUint(string memory nameOfData, bytes32 key, address payable locker, uint data)
    public returns(LockStatus lockStatus, uint256 revertData)
    {
        lockStatus = isLocked(nameOfData, key, locker);
        if(lockStatus != LockStatus.UserWithRevert){
            bytes memory tmp = new bytes(32);
            assembly { mstore(add(tmp, 32), data) }
            Type dataType = Type.Uint;
            saveData(nameOfData, key, tmp, dataType);
        } else {
            DataRecord memory record = getDataRecord(nameOfData, key);
            if(record.dataType == Type.Uint) {
                revertData = revertDataUint(record.dataBeforeLock);
            }
        }

        _lock(nameOfData, key, locker);
    }

    function revertDataUint(bytes memory data) internal view returns(uint _output) {
        assembly {
            _output := mload(add(data, 0))
        }
    }



    //Checks whether key of data structure `nameOfData` is locked.

    function isLocked(string memory nameOfData, bytes32 key, address payable locker) 
    public returns(LockStatus)
    {

        address payable currLocker = getCurrLocker(nameOfData, key);

        //if currLocker is 0x0 then it is obviously not locked.
        if(isZeroAddr(locker)) return (LockStatus.User);


        //Checks if `currLocker` has expired, if so, proceed to unlock everything
        // if `currLocker` is `locker` with expired lock, will treat as though its a new
        // lock.

        if(isExpired(currLocker)){

            //This is considered a dirty unlock since it wasn't done requested by the user itself.
            unlock(currLocker);
            return (LockStatus.UserWithRevert);
        }

        //At this point, currLocker != 0 and !expired, so we require that the
        //currLocker has to be the locker, else revert all actions.

        require(currLocker == locker, "currLocker has to be locker");
        return (LockStatus.User);
    }


    function _lock(string memory nameOfData, bytes32 key, address payable locker) 
    internal returns(bool)
    {   
        //Assumes at this point that the timeout has not exceeded yet, since isExpired 
        //is queried in isLocked.
        bytes32 hashKey = getHashKey(nameOfData);
        //Since timeout has not exceeded, if locker is active there is no need 
        // to relock. Just return it.
        if(dataRecords[hashKey].records[key].lockStatus == locker) return true; 

        //Set lock onto the key of specific data structure hash key.
        dataRecords[hashKey].records[key].lockStatus = locker;

        //Add record of lock into address records
        userRecords[locker].records.push(Record({
            data : hashKey,
            key : key
        }));

        //Adds expiry time of lock
        //assert(userRecords[locker].expiry == 0);
        userRecords[locker].expiry = block.number + TIMEOUT;

        return true;
    }

    function lockWithDeposit(string memory nameOfData, bytes32 key, address payable locker)
    payable public hasEnoughDeposit(locker)
    {
        userRecords[locker].deposit = msg.value;
        _lock(nameOfData, key, locker);
    }

    function lockSingleWithDeposit(string memory nameOfData, address payable locker)
    payable public hasEnoughDeposit(locker)
    {
        userRecords[locker].deposit = msg.value;
        lockSingle(nameOfData, locker);
    }

    function unlock(address payable locker)
    internal
    {
        Record[] memory UserRecords = userRecords[locker].records;
        for (uint i = 0; i < UserRecords.length; i++) {
            Record memory curr = UserRecords[i];
            _unlock(curr.data, curr.key);   
        }
        userRecords[locker].lastLockTime = block.number;
        delete userRecords[locker].records;
        delete userRecords[locker].expiry;
        refundDeposit(locker);
    }

    function refundDeposit(address payable locker)
    internal
    {
        uint256 refund = userRecords[locker].deposit;
        if(scaleRefund){
            refund = calculateScaleRefund(locker, refund);
        }

        locker.transfer(refund);
        delete userRecords[locker].deposit;
    }

    function lockSingle(string memory nameOfData, address payable locker)
    public returns(bool)
    {
        return _lock(nameOfData, bytes32(0), locker);
    }



    //
    //
    //



    //
    // Helper functions and modifiers
    //
    function getHashKey(string memory arb) 
    pure internal returns (bytes32)
    {
        return keccak256(abi.encodePacked(arb));
    }

    function getCurrLocker(string memory nameOfData, bytes32 key) 
    public view returns(address payable){
        bytes32 hashKey = getHashKey(nameOfData);
        return dataRecords[hashKey].records[key].lockStatus;
    }


    function _unlock(bytes32 nameOfDataKey, bytes32 hashKey)
    private
    {   
        delete dataRecords[nameOfDataKey].records[hashKey].lockStatus;
    }

    modifier hasEnoughDeposit(address locker) 
    {
        uint256 deposit = DEPOSIT_FEE;
        //Calculate what the cooling off time is. If COOLING_OFF is 0, which means that cooling off is switched
        //off, then it will always be the base deposit of DEPOSIT_FEE.
        uint256 coolingOffTime = userRecords[locker].lastLockTime + COOLING_OFF;

        if(block.number < coolingOffTime){
            deposit *= 2 ** (coolingOffTime - block.number);
        }
        require(msg.value >= deposit);
        _;
    }

    function isExpired(address locker) 
    view public returns(bool)
    {   

        return(userRecords[locker].expiry < block.number);
    }

    function expBlock(address locker) view public returns (uint)
    {
        return(userRecords[locker].expiry);
    }

    function lastLock(address locker) view public returns (uint)
    {
        return(userRecords[locker].lastLockTime);
    }

    function calculateScaleRefund(address locker, uint256 deposit)
    internal view returns(uint256)
    {
        uint256 expiry = userRecords[locker].expiry;
        if(block.number > userRecords[locker].expiry){
            return 0;
        } 
        return deposit * (expiry - block.number) / TIMEOUT;

    }

    function isZeroAddr(address payable addr) internal pure returns (bool) {
        return(addr == address(0));
    }
   //
   // Debug functions
   //
    function getDataRecord(string memory nameOfData, bytes32 key) internal view returns(DataRecord memory) {
        return dataRecords[getHashKey(nameOfData)].records[key];
    }


}
